import { useState } from "react";
import "./ExpenseFilter.css";

const ExpensesFilter = (props) => {
  const [filter, setfilter] = useState({
    year: props.selectedYear,
    month: props.selectedMonth,
  });
  const yearChangeHandler = (event) => {
    // const year = event.target.value;
    setfilter({ year: event.target.value, ...filter });
    // props.onChangeFilter(year, props.selectedMonth);
    console.log(filter);
  };
  const MonthChangeHandler = (event) => {
    const month = event.target.value;
    props.onChangeFilter(props.selectedYear, props.selectedMonth);
    console.log(props.selectedYear, month);
  };
  // props.onChangeFilter(year,month);
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter</label>
        <select value={props.selectedYear} onChange={yearChangeHandler}>
          <option value="2023">2023</option>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
        </select>
        <select value={props.selectedMonth} onChange={MonthChangeHandler}>
          <option value="1">January</option>
          <option value="2">February</option>
          <option value="3">March</option>
          <option value="4">April</option>
          <option value="5">May</option>
          <option value="6">June</option>
          <option value="7">July</option>
          <option value="8">August</option>
          <option value="9">September</option>
          <option value="10">October</option>
          <option value="11">November</option>
          <option value="12">December</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
