import { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";

const Expenses = (props) => {
  const date = new Date();
  const month = date.getMonth();
  const [filter, setFilter] = useState({
    month: month,
    year: "2022",
  });
  // const [filteredMonth, setfilteredMonth] = useState(month);

  const filteredExpenses = props.items.filter((expense) => {
    return (
      expense.date.getFullYear().toString() === filter.year &&
      expense.date.getMonth().toString() === filter.month
    );
  });

  let expenseItemContent = <p>No Expense Found</p>;
  if (filteredExpenses.length > 0) {
    expenseItemContent = filteredExpenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  const filterChangeHandler = (selectedYear, selectedMonth) => {
    setFilter({
      month: selectedMonth,
      year: selectedYear,
    });
    // setfilter(selectedMonth);
    console.log(selectedYear, selectedMonth);
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selectedYear={filteredYear}
          selectedMonth={filteredMonth}
          onChangeFilter={filterChangeHandler}
        />
        {expenseItemContent}
      </Card>
    </div>
  );
};

export default Expenses;
